// 利用可能なキーイベントをまとめた関数
// case 39,37はスライドを移動させるためのキー
// case 68は選択されたフォーム（アンケートフォームや感想フォーム）を削除するためのキー
// $function()はjqueryのAPIを使える

// 要素のidと選択されたか否かを記録する変数
// ある要素が選択された場合の例：["opinion-1",true]
var selectedId = ["id",false];

/**
フォームをスライドごとにセットする方法
1.キーと値の配列を作る
2.フォームをセットした時、フォームidと現在のスライドidをキーと値として関連づける
3.スライドを移動した時、現在のスライドidを取得し、配列を全て検索してスライドidと関連づけられているフォームidが存在するか調べる
4.存在すればそのフォームを表示させる
5.それ以外のフォームは非表示にする
**/

//1.キーと値の連想配列を作る （変数名が思いつかない）
var slideIdAndFormId = {};
$(function(){
	// キーイベント処理
	$(window).keyup(function(e){
		switch(e.which){

			// →を押したら、押されたことをサーバに伝える
			case 39:
			// スライドを次のページに進めるメッセージ
			socket.emit('nextpage_from_client');
			break;

			// ←を押したら、押されたことをサーバに伝える
			case 37:
			// スライドを前のページに戻すメッセージ
			socket.emit('prepage_from_client');
			break;

			// フォームが選択された状態でdを押すと、選択されたフォームが削除される
			case 68:
			if(selectedId[1]==true){
				removeForm(selectedId);
				//socket.emit('rmForm_from_client',selectedId);
			}
			break;

			// フォームが選択された状態でEnterを押すと、現在のスライドに選択されたフォームが設定される
			case 13:
			if(selectedId[1]==true){
				setForm(selectedId);
				//socket.emit('setForm_from_client',selectedId);
				alert("選択中のフォームを現在のスライドにセットしました。");
			}
			break;
		}
	});
});
