// スライドを次のページに進める
function nextPage(){
	// スライドを移動させるたびにjsonObjに記録して、json.txtとして書き出さないと途中でアクセスした人とスライドを同期できない
	var last = jsonObj["slide"].pop();
	jsonObj["slide"].unshift(last);
	socket.emit('save_from_client',jsonObj);

	var jsonObjLength = jsonObj["slide"].length - 1;
	$('#slideimg').attr({
		"src":jsonObj["slide"][jsonObjLength]["src"],
		"zIndex":jsonObj["slide"][jsonObjLength]["zIndex"]
	});
	// 3.スライドを移動した時、現在のスライドidを取得し、
	// 連想配列を全て検索して、取得したスライドidをキーとする値（フォームid）が存在するか調べる
	var key = $('#slide').find(":last").attr("zIndex");
	for (var keyString in slideIdAndFormId) {
		// 4.存在すればそのフォームを表示させる
		// 5.それ以外のフォームは非表示にする
		if(key == keyString){
			$("#"+slideIdAndFormId[keyString]).css("visibility","visible");
		}else{
			$("#"+slideIdAndFormId[keyString]).css("visibility","hidden");
		}
	}
}
