// 保存されたデータをファイルとして出力するプログラムが必要
var jsonObj = {
	slide:[],
	questions:[],
	comments:[],
};
function slideLoad(length){
	for (var i = length; i > 0; i--) {
		var img = $('<img>').attr({
			class:"step",
			src:'/../img/'+String(i)+'.jpg',
			//title:(i+1)+'.png',
			zIndex:i
		});
		var imgObj = {src:img.attr('src'),zIndex:img.attr('zIndex')};
		jsonObj["slide"].push(imgObj);
		$('#slide').append(img);
	}
}